import { useForm } from 'react-form-simple';
export default function App() {
  const { render, modal } = useForm({ name: '' });
  const renderName = render('name')(<input />);
  const renderSubmit = (
    <button
      onClick={() => {
        console.log({ modal });
      }}
    >
      submit
    </button>
  );
  return (
    <>
      {renderName}
      {renderSubmit}
    </>
  );
}
